<?php
/**
 * PayPal payout method
 * 
 * @package EDD Commissions Payouts
 */

if ( ! defined( 'ABSPATH' ) ) {
	exit;
}

class EDD_Commissions_Payout_Method_PayPal extends EDD_Commissions_Payouts_Method {

    public function __construct() {
        $this->id           = 'paypal';
        $this->name         = __( 'PayPal', 'edd-commissions-payouts' );
        $this->icon         = 'data:image/svg+xml;base64,';
    
        if ( function_exists( 'EDD_FES' ) ) {
            $this->enable_uri   = get_permalink( EDD_FES()->helper->get_option( 'fes-vendor-dashboard-page', get_permalink() ) );
            $this->redirect_uri = get_permalink( EDD_FES()->helper->get_option( 'fes-vendor-dashboard-page', get_permalink() ) );
        }else {
            $this->enable_uri   = home_url();
            $this->redirect_uri = home_url();
        }
    }
}